mapper.select:
select rno, category, fk_name, buy_date, unable_date, discount_money, coupon_no, fk_email, status from ( select rownum as RNO, V.fk_name, V.category, V.buy_date, V.unable_date, V.discount_money, V.coupon_no, V.fk_email, V.status from ( select a.category,a.fk_name, to_char(b.buy_date, 'yyyy-mm-dd') as buy_date, to_char(b.unable_date, 'yyyy-mm-dd') as unable_date, b.discount_money, b.coupon_no, b.fk_email, b.status from tbl_store a join tbl_store_history b on a.store_eventno=b.fk_store_eventno where fk_email = #{email} order by buy_date desc )V )T where rno between #{startrno} and #{endrno}
and category in (1,3)
and category in (2,4)

select count(*) as totalcnt
from tbl_store_history
where fk_email = #{email}
